onsubmit_button.html('<img class="icon_loading" src="' + baseurl + 'themes/default/images/ajax_loading.gif"/><span>' + L['cy_saving'] + '...</span>');            var ispass = 1;            if (ispass == 1) {                $.post(baseurl + 'index.php/admins/order/edit_order/' + order_id, $("#testform").serialize(), function (data) {                    var obj = eval("(" + data + ")");                    actionsubmit_button.html('<img class="icon_success" src="' + baseurl + 'themes/default/images/global_ok.png"/><span>' + L['cy_save_success'] + '</span>');                    location.href = obj.backurl;                })            } else {                actionsubmit_button.attr('class', 'gksel_btn_action_on');                actionsubmit_button.html(L['cy_save']);                isajaxsaveing = 0;//ajax正在保存中 --- 释放            }        }    }</script><?php $this->load->view('admin/footer') ?>